% returns single shape of given set

get_shapes(SETNAME,SHAPE):-
	shapelist(SETNAME,SHAPELIST),
	length(SHAPELIST,L),
	L2 is L + 1,
	random(R),
	mod(R,L2,I),

	nth(I,SHAPELIST,SHAPE).

% returns all shapes of given set

get_shapelist(SETNAME,SHAPELIST):-
	bagof(SHAPE,shape(SETNAME,SHAPE),SHAPELIST).

% get_boundingboxes([],[]).

% get_boundingboxes([SHAPE|TAIL],[[SHAPE,BB]|TAIL2]):-
	% get_BB(SHAPE,BB),
	% writeln(BB),
	% get_boundingboxes(TAIL, TAIL2).

% returns bounding box of polygon

get_BB(SHAPE,[W,H]):-
	getBBCoord(xmin,SHAPE,XMIN),
	getBBCoord(ymin,SHAPE,YMIN),
	getBBCoord(xmax,SHAPE,XMAX),
	getBBCoord(ymax,SHAPE,YMAX),
	W is XMAX - XMIN,
	H is YMAX - YMIN.


translate_points(_,_,[],[]).

translate_points(X,Y,[Head|Tail],[Point|Rest]):-
	translate_point(X,Y,Head,Point),
	translate_points(X,Y,Tail,Rest).
 
translate_point(X,Y,[X1,Y1],[X2,Y2]):-
	padding(Padding),
	X2 is X + X1 + Padding,
	Y2 is Y + Y1 + Padding.



rescale_points(_,[],[]).

rescale_points(Factor,[Head|Tail],[Point|Rest]):-
	rescale_point(Factor,Head,Point),
	rescale_points(Factor,Tail,Rest).

rescale_point(Factor,[X1,Y1],[X2,Y2]):-
	X2 is Factor * X1,
	Y2 is Factor * Y1.

rescale_values(_,[],[]).

rescale_values(Factor,[V1|Tail],[V2|Rest]):-
	V2 is Factor * V1,
	rescale_values(Factor,Tail,Rest).


getBBCoord(xmin,[[X,_]|[]],X).

getBBCoord(xmin,[[X1,_]|Tail],X3):-
	getBBCoord(xmin,Tail,X2),
	min(X1,X2,X3).


getBBCoord(xmax,[[X,_]|[]],X).

getBBCoord(xmax,[[X1,_]|Tail],X3):-
	getBBCoord(xmax,Tail,X2),
	max(X1,X2,X3).


getBBCoord(ymin,[[_,Y]|[]],Y).

getBBCoord(ymin,[[_,Y1]|Tail],Y3):-
	getBBCoord(ymin,Tail,Y2),
	min(Y1,Y2,Y3).


getBBCoord(ymax,[[_,Y]|[]],Y).

getBBCoord(ymax,[[_,Y1]|Tail],Y3):-
	getBBCoord(ymax,Tail,Y2),
	max(Y1,Y2,Y3).
